    },
    allowNull: false,
    onUpdate: "cascade",
    onDelete: "cascade"
  },
  type_id: {
    type: DataTypes.INTEGER,
    references: {
      model: {
        tableName: "tool_aiedutech"
      },
      // provide table name
      key: "id"
    },
    allowNull: false,
    onUpdate: "cascade",
    onDelete: "cascade"
  }
}, {
  timestamps: false,
  tableName: "op_exp_tech_type",
  modelName: "opExpTechType"
});
const ClassTopic = sequelize.define("ClassTopic", {
  id: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
  name: DataTypes.TEXT,
  created_at: DataTypes.TIME
}, {
  timestamps: false,
  tableName: "class_topic",
  modelName: "ClassTopic"
});
const AiedutechTool = sequelize.define("AiedutechTool", {
  sno: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
  name_kr: DataTypes.TEXT,
  name_en: DataTypes.TEXT,
  tool_id: DataTypes.NUMBER,
  tech_id: DataTypes.NUMBER,
  core_func: DataTypes.TEXT,
  description: DataTypes.TEXT,
  service_link: DataTypes.TEXT,
  case_link: DataTypes.TEXT,
  case_img: DataTypes.TEXT
}, {
  timestamps: false,
  tableName: "aiedutech_tool",
  modelName: "AiedutechTool"
});
const LessonSample = sequelize.define("LessonSample", {
  sno: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
  topic: DataTypes.TEXT,
  topic_id: {
    type: DataTypes.INTEGER,
    references: {
      model: {
        tableName: "class_topic"
      },
