  difficulty: DataTypes.TEXT,
  objective: DataTypes.TEXT,
  achievement: DataTypes.TEXT,
  source: DataTypes.TEXT,
  pdf_link: DataTypes.TEXT
}, {
  timestamps: false,
  tableName: "class_case",
  modelName: "LessonSample"
});
LessonSample.belongsTo(ClassTopic, {
  foreignKey: "topic_id",
  as: "classTopic"
});
const ModelSearch = sequelize.define("ModelSearch", {
  id: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
  name: DataTypes.TEXT,
