    type: DataTypes.INTEGER,
    references: {
      model: {
        tableName: "class_type"
      },
      key: "id"
    },
    allowNull: false,
    onUpdate: "cascade",
    onDelete: "cascade"
  },
  subject_id: {
    type: DataTypes.INTEGER,
    references: {
      model: {
        tableName: "class_subject"
      },
      key: "id"
    },
