});
//Many-to-Many Association LessonSample, tool

const ClassCaseTool = sequelize.define("ClassCaseTool", {
  id: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
