            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "op_exp" }, // provide table name
                key: "sno",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
        type_id: {
            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "tool_aiedutech" }, // provide table name
                key: "id",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
    },
    {
        timestamps: false,
