const Community = require("../models/communityModel");
const Comment = require("../models/commentModel");
const User = require("../models/usedModel");
const PostReaction = require("../models/communityReactionModel");

exports.getAllWithUserID = async (user_id) => {
    return await Community.findAll({
        where: { user_id },
        include: ["comments", "user"],
    });
};
exports.getById = async (sno) => {
    let data = await Community.findOne({
        where: { sno },
        include: [
            {
                model: Comment,
                as: "comments",
                include: ["user"],
            },
            {
                model: User,
                as: "user",
                attributes: [
                    "id",
                    "name",
                    "email",
                    "profile",
                    "types",
                    "school",
                    "subjects",
                    "teacher_type",
                ],
            },
            {
                model: PostReaction,
                as: "reactions",
            },
        ],
    });
    let newDataPost = { ...data.get({ plain: true }) };
    //handle comment
    let dataComments = data.comments.map((result) =>
        result.get({ plain: true })
    );

    const likes = data.reactions.filter((reaction) => reaction.type === "like");
    const dislikes = data.reactions.filter(
        (reaction) => reaction.type === "dislike"
    );
    newDataPost["reactions"] = { likes, dislikes };

    await processComments(dataComments).then((newComments) => {
        newDataPost["comments"] = newComments;
    });

    return newDataPost;
};
async function processComments(dataComments) {
    const newComments = [];

    await Promise.all(
        dataComments.map(async (comment) => {
            let newDataComment = { ...comment };
            if (comment.parent_comment_id == null) {
                const replies = dataComments
                    .filter((item) => item.parent_comment_id === comment.id)
                    .map((reply) => ({ ...reply }));
                newDataComment["replies"] = replies;
                newComments.push(newDataComment);
            }
        })
    );

    return newComments;
}
exports.getAllWithType = async (type) => {
    return await Community.findAll({
        where: { type },
    });
};
exports.getAll = async ({ paged, limit, filter, type }) => {
    const offset = (paged - 1) * limit;
    let query = {
        include: [
            {
                model: User,
                as: "user",
                attributes: [
                    "id",
                    "name",
                    "email",
                    "profile",
                    "types",
                    "school",
                    "subjects",
                    "teacher_type",
                ],
            },
        ],
        order: [["createdAt", "DESC"]],
    };

    if (paged) {
        query["offset"] = offset;
    }
    if (limit) {
        query["limit"] = limit;
    }
    if (filter) {
        switch (filter) {
            case "class_type":
        }
    }
    if (type) {
        query["where"] = { type };
    }

    const { count, rows } = await Community.findAndCountAll(query);

    const totalPages = Math.ceil(count / limit);

    return {
        items: rows,
        totalItems: count,
        itemCount: rows.length,
        itemsPerPage: limit,
        totalPages,
        currentPage: paged,
    };
};

exports.createPost = async (
    title,
    content,
    class_type,
    subject,
    grade,
    user_id,
    attachments,
    type
) => {
    let suggestion = { listUserId: [] };
    let data = await Community.create({
        sno: null,
        title,
        content,
        class_type,
        subject,
        grade,
        user_id,
        attachments: { attachments },
        suggestion,
        type,
        viewers: 0,
    });

    return data;
};

exports.updateSuggestionOrViewers = async (
    suggestion,
    viewers,
    user_id,
    post_id,
    type
) => {
    let data = await Community.findOne({
        where: { sno: post_id },
        include: [
            {
                model: PostReaction,
                as: "reactions",
            },
        ],
        attributes: [
            "sno",
            "class_type",
            "type",
            "subject",
            "grade",
            "title",
            "content",
            "user_id",
            "viewers",
            "createdAt",
            "createdAt",
            "suggestion",
        ],
    });
    if (data == null) {
        throw new Error("The post not exits!");
    }
    let typeReaction = ["like", "dislike"];
    if (typeReaction.includes(type)) {
        let [reaction, created] = await PostReaction.findOrCreate({
            where: { post_id, user_id },
            defaults: {
                id: null,
                post_id,
                user_id,
                type,
            },
        });
        if (!created) {
            if (type != reaction.type) {
                reaction.type = type;
                reaction.save({ fields: ["type"] });
            } else {
                let typeReaction = ["like", "dislike"];
                if (typeReaction.includes(type)) {
                    await PostReaction.destroy({ where: { id: reaction.id } });
                }
            }
        }
    }
    if (type == "viewers") {
        let listUserId = data.suggestion.listUserId;
        if (!listUserId.includes(user_id)) {
            listUserId.push(user_id);
            let suggestion = { listUserId: listUserId };
            data.viewers = data.viewers + 1;
            data.suggestion = suggestion;
            data.save({ fields: ["viewers", "suggestion"] });
        }
    }

    return data;
};
