exports.formatData = async (req, res, next) => {
  try {
    const filePath = path.join(__dirname, "../../dataRow.json");
    let aData = [];
    const data = await readFile(filePath, "utf8");
    const jsonData = JSON.parse(data);
    await Promise.all(jsonData.map(async item => {
      aData.push({
        id: item[0],
        name: item[1],
        class_topic: [item[2], item[3], item[4], item[5], item[6]],
        chasi: item[7],
        class_type: [item[8], item[9], item[10]],
