const {
  sequelize,
  QueryTypes,
  LessonSample,
  ModelSearch,
  AiedutechTool,
  OperationCase,
  OperationTool,
  OperationGrade,
  OperationType,
  OperationSubject,
  OperationTopic
} = require("../models/searchModel");
const Wishlist = require("../models/wishlistModel");
const {
  Topic,
  Subject,
  Grade,
  Type
} = require("../models/designModel");
const User = require("../models/usedModel");
const {
  SurveyResult,
  SurveyQuestion
} = require("../models/surveyModel");
const {
  faker
} = require("@faker-js/faker");
const bcrypt = require("bcryptjs");
exports.searchLessons = async ({
  paged,
  limit
}) => {
  const offset = (paged - 1) * limit;
  let query = {
    include: "classTopic"
  };
  if (paged) {
    query["offset"] = offset;
  }
  if (limit) {
    query["limit"] = limit;
  }
  const {
    count,
    rows
  } = await LessonSample.findAndCountAll(query);
  const totalPages = Math.ceil(count / limit);
  return {
    items: rows,
    totalItems: count,
    itemCount: rows.length,
    itemsPerPage: limit,
    totalPages,
    currentPage: paged
  };
};
exports.searchOpExp = async () => {
  return await sequelize.query("select `sno` as `sno`, `name_kr` as `name`, `name_en` as `nameEn`, `description` as `description`, `case_img` as `img`, `tool_aiedutech`.`name` as `toolTp`, `tech_id` as `tech`, group_concat(op_exp_tech_type.type_id) as 'techTp' from `op_exp` left join `op_exp_tech_type` on `op_exp_tech_type`.`exp_sno` = `op_exp`.`tech_id` left join `tool_aiedutech` on `tool_aiedutech`.`id` = `op_exp`.`tool_id` group by `sno`", {
    type: sequelize.QueryTypes.SELECT
  });
};
exports.searchCaseFilter = async () => {
  return await sequelize.query("select *, 'topic' as type from `class_topic` union select *, 'type' as type from `class_type` union select *, 'subject' as type from `class_subject` union select *, 'grade' as type from `class_grade`", {
    type: sequelize.QueryTypes.SELECT
  });
};
exports.modelFilter = async () => {
  return await sequelize.query("select *, 'type' as type from `class_type` union select *, 'subject' as type from `class_subject` union select *, 'grade' as type from `class_grade`", {
    type: sequelize.QueryTypes.SELECT
  });
};
exports.searchCase = async user_id => {
  let data = await sequelize.query("select `class_case`.`sno`, `class_case`.`topic`, `class_case`.`objective`, `class_case`.`time`, `class_case`.`topic_id` as `topicTp`, `class_case`.`difficulty`, `class_case`.`achievement`, `class_case`.`source`, `class_case`.`pdf_link` as `pdf`, group_concat(distinct class_case_type.type_id) as 'type', group_concat(distinct class_case_subject.subject_id) as 'subject', group_concat(distinct class_case_grade.grade_id) as 'grade', group_concat(distinct class_case_tool.tool_sno) as 'tool', group_concat(distinct aiedutech_tool.name_kr) as 'toolNm' from `class_case` left join `class_case_type` on `class_case_type`.`case_sno` = `class_case`.`sno` left join `class_case_subject` on `class_case_subject`.`case_sno` = `class_case`.`sno` left join `class_case_grade` on `class_case_grade`.`case_sno` = `class_case`.`sno` left join `class_case_tool` on `class_case_tool`.`case_sno` = `class_case`.`sno` left join `aiedutech_tool` on `class_case_tool`.`tool_sno` = `aiedutech_tool`.`sno` group by `class_case`.`sno`", {
    type: sequelize.QueryTypes.SELECT
  });
  let type = "lesson";
  let wishlistTool = await Wishlist.findAll({
    where: {
      user_id,
      type
    },
    attributes: ["foreign_key_id"]
  });
  let foreignKeyIds = wishlistTool.map(item => item.foreign_key_id);
  const processedData = await Promise.all(data.map(async item => {
    item["isWishlist"] = foreignKeyIds.includes(item.sno);
    return item;
  }));
  return processedData;
};
exports.searchModel = async id => {
  let data = await ModelSearch.findOne({
    include: [{
      model: Type,
      as: "type"
    }, {
      model: Subject,
      as: "subject"
    }],
    where: [id]
  });
  if (data != null) {
    let pdf = process.env.BASIC_URL + "/image/model/" + data.pdf_link;
