  let wishlistTool = await Wishlist.findAll({
    where: {
      user_id,
      type
    },
    attributes: ["foreign_key_id"]
  });
  let foreignKeyIds = wishlistTool.map(item => item.foreign_key_id);
  data = data.map(result => result.get({
    plain: true
  }));
  //convert pdf link
  const processedData = await Promise.all(data.map(async item => {
    // Update the pdf_link
    let pdf = process.env.BASIC_URL + "/image/model/" + item.pdf_link;
    item.pdf_link = pdf;

    // Process grades
    if (item.grade) {
      let dataGrade = await Promise.all(item.grade.map(async name => {
        return await Grade.findOne({
          where: {
            name
          }
        });
      }));
      item.grade = dataGrade;
    }
    item["isWishlist"] = foreignKeyIds.includes(item.id);
    return item;
  }));
