};
exports.toolFilter = async () => {
  let data = await sequelize.query("select `tool_aiedutech_tech_type`.`tool_aiedutech_tech_id` as `techId`, `tool_aiedutech_tech_type`.`name` as `name`, `tool_aiedutech_tech`.`tool_aiedutech_id` as `aiedutechId`, `tool_aiedutech_tech_type`.`id` as `techTpId` from `tool_aiedutech_tech_type` inner join `tool_aiedutech_tech` on `tool_aiedutech_tech`.`id` = `tool_aiedutech_tech_type`.`tool_aiedutech_tech_id` union select `tool_aiedutech_tech`.`id` as `techId`, `tool_aiedutech_tech`.`name` as `name`, `tool_aiedutech_tech`.`tool_aiedutech_id` as `aiedutechId`, '' as techTpId from `tool_aiedutech_tech` order by `aiedutechId` asc, `techId` asc", {
    type: sequelize.QueryTypes.SELECT
  });
  return filterTool(data);
};
const filterTool = data => {
  let rst = [];
  let child = [];
  for (const row of data) {
    if (row.techTpId === "") {
      rst.push(row);
    } else {
      child.push(row);
    }
  }
  for (const row of child) {
    for (const parent of rst) {
      if (row.techId === parent.techId) {
        if (parent.child) {
          parent.child.push(row);
        } else {
          parent.child = [row];
        }
      }
    }
  }
  return [rst.filter(row => row.aiedutechId === 1), rst.filter(row => row.aiedutechId === 2)];
};
