exports.handleInsertDataOperationCase = async data => {
  await Promise.all(data.map(async item => {
    //insert operation_case
    const Operation = await OperationCase.create({
      id: null,
      title: item.name,
      chasi: item.chasi,
      difficulty: item.difficulty,
      learning_objectives: item.learning_objectives,
      achievement_criteria: item.achievement_criteria,
      source: item.source,
      pdf_link: item.pdf,
      source: item.source
    });
    let operation_id = Operation.id;
    if (operation_id) {
      //topic
      item["class_topic"].map(async topicName => {
        const [topic, created] = await Topic.findOrCreate({
          where: {
            name: topicName
          },
          defaults: {
            id: null,
            name: topicName
          }
        });
        let topic_id = topic.id;
        if (topic_id) {
          OperationTopic.create({
            id: null,
            topic_id,
            operation_id
          });
        }
      });
    }
    if (operation_id) {
      //type
      item["class_type"].map(async typeName => {
        const [type, created] = await Type.findOrCreate({
          where: {
            name: typeName
          },
          defaults: {
            id: null,
            name: typeName
          }
        });
        let type_id = type.id;
        if (type_id) {
          OperationType.create({
            id: null,
            type_id,
            operation_id
          });
        }
      });
    }
    if (operation_id) {
      //subject
      item["class_subject"].map(async subjectName => {
        const [subject, created] = await Topic.findOrCreate({
          where: {
            name: subjectName
          },
          defaults: {
            id: null,
            name: subjectName
          }
        });
        let subject_id = subject.id;
        if (subject_id) {
          OperationSubject.create({
            id: null,
            subject_id,
            operation_id
          });
        }
      });
    }
    if (operation_id) {
      //grade
      item["class_grade"].map(async gradeName => {
        const [grade, created] = await Grade.findOrCreate({
          where: {
            name: gradeName
          },
          defaults: {
            id: null,
            name: gradeName
          }
        });
        let grade_id = grade.id;
        if (grade_id) {
          OperationGrade.create({
            id: null,
            grade_id,
            operation_id
          });
        }
      });
    }
    if (operation_id && item["artificial_intelligence_tools"]) {
      //tool
      item["artificial_intelligence_tools"].map(async id => {
        const tool = await AiedutechTool.findOne({
          where: {
            sno: id
          }
        });
        if (tool) {
          OperationTool.create({
            id: null,
            tool_id: id,
            operation_id: operation_id
          });
        }
      });
    }
  }));
};
