async function handlePointAnswers(answers) {
  //condition
  let conditionPoint = 6;
  let basic_utilization_avg = 0;
  let basic_utilization_total = 0;
  let basic_utilization_question_count = 0;
  //
  let basic_practice_avg = 0;
  let basic_practice_total = 0;
  let basic_practice_question_count = 0;
  //
  let edu_use_avg = 0;
  let edu_use_total = 0;
  let edu_use_question_count = 0;
  //
  let edu_teaching_avg = 0;
  let edu_teaching_total = 0;
  let edu_teaching_question_count = 0;
  //
  let edu_dev_avg = 0;
  let edu_dev_total = 0;
  let edu_dev_question_count = 0;
  //
  let edu_implementation_avg = 0;
  let edu_implementation_total = 0;
  let edu_implementation_question_count = 0;
  //
  let edu_evaluation_avg = 0;
  let edu_evaluation_total = 0;
  let edu_evaluation_question_count = 0;
  //
  let professional_dev_avg = 0;
  let professional_dev_total = 0;
  let professional_dev_question_count = 0;
  //
  let professional_social_avg = 0;
  let professional_social_total = 0;
  let professional_social_question_count = 0;
  //
  let zone_competency_avg = 0;
  let zone_edu_avg = 0;
  let zone_professional_avg = 0;
  let zone_avg = 0;

  //
  let point_avg = 0;
  let point_total = 0;
  await Promise.all(answers.map(async answer => {
    //find question
    let question = await SurveyQuestion.findOne({
      attributes: ["zone_competency"],
      where: {
        id: answer.question_id
      }
    });
    if (question == null) {
      throw new Error("The question not exist!");
    }
    switch (question.zone_competency) {
      case "basic_utilization":
        basic_utilization_total += answer.answer_id;
        basic_utilization_question_count++;
        break;
      case "basic_practice":
        basic_practice_total += answer.answer_id;
        basic_practice_question_count++;
        break;
      case "edu_use":
        edu_use_total += answer.answer_id;
        edu_use_question_count++;
        break;
      case "edu_teaching":
        edu_teaching_total += answer.answer_id;
        edu_teaching_question_count++;
        break;
      case "edu_dev":
        edu_dev_total += answer.answer_id;
        edu_dev_question_count++;
        break;
      case "edu_implementation":
        edu_implementation_total += answer.answer_id;
        edu_implementation_question_count++;
        break;
      case "edu_evaluation":
        edu_evaluation_total += answer.answer_id;
        edu_evaluation_question_count++;
        break;
      case "professional_dev":
        professional_dev_total += answer.answer_id;
        professional_dev_question_count++;
        break;
      case "professional_social":
        professional_social_total += answer.answer_id;
        professional_social_question_count++;
        break;
    }
    point_total = point_total + answer.answer_id;
  }));
  basic_utilization_avg = roundToDecimalPlace(basic_utilization_total / basic_utilization_question_count, 1);
  basic_practice_avg = roundToDecimalPlace(basic_practice_total / basic_practice_question_count, 1);
  edu_use_avg = roundToDecimalPlace(edu_use_total / edu_use_question_count, 1);
  edu_teaching_avg = roundToDecimalPlace(edu_teaching_total / edu_teaching_question_count, 1);
  edu_dev_avg = roundToDecimalPlace(edu_dev_total / edu_dev_question_count, 1);
  edu_implementation_avg = roundToDecimalPlace(edu_implementation_total / edu_implementation_question_count, 1);
  edu_evaluation_avg = roundToDecimalPlace(edu_evaluation_total / edu_evaluation_question_count, 1);
  professional_dev_avg = roundToDecimalPlace(professional_dev_total / professional_dev_question_count, 1);
  professional_social_avg = roundToDecimalPlace(professional_social_total / professional_social_question_count, 1);
  zone_basic_avg = roundToDecimalPlace((basic_practice_total + basic_utilization_total) / (basic_practice_question_count + basic_utilization_question_count), 1);
  zone_edu_avg = roundToDecimalPlace((edu_dev_total + edu_teaching_total + edu_use_total + edu_implementation_total + edu_evaluation_total) / (edu_dev_question_count + edu_teaching_question_count + edu_use_question_count + edu_implementation_question_count + edu_evaluation_question_count), 1);
  zone_professional_avg = roundToDecimalPlace((professional_dev_total + professional_social_total) / (professional_dev_question_count + professional_social_question_count), 1);
  zone_avg = roundToDecimalPlace((zone_basic_avg + zone_edu_avg + zone_professional_avg) / 3, 1);
  zone_competency_avg = roundToDecimalPlace((basic_utilization_avg + basic_practice_avg + edu_use_avg + edu_teaching_avg + edu_dev_avg + edu_implementation_avg + edu_evaluation_avg + professional_dev_avg + professional_social_avg) / 9, 1);
  point_avg = roundToDecimalPlace(point_total / 30, 1);
  return {
    basic_utilization_avg,
    basic_practice_avg,
    edu_use_avg,
    edu_teaching_avg,
    edu_dev_avg,
    edu_implementation_avg,
    edu_evaluation_avg,
    professional_dev_avg,
    professional_social_avg,
    zone_competency_avg,
    zone_basic_avg,
    zone_edu_avg,
    zone_professional_avg,
    zone_avg,
    point_avg
  };
}
function roundToDecimalPlace(number, decimalPlaces) {
  const factor = Math.pow(10, decimalPlaces);
  return Math.round(number * factor) / factor;
}
