exports.handleInsertDataOperationCase = async (data) => {
    await Promise.all(
        data.map(async (item) => {
            //insert operation_case
            const Operation = await OperationCase.create({
                id: null,
                title: item.name,
                chasi: item.chasi,
                difficulty: item.difficulty,
