                achievement_criteria: item.achievement_criteria,
                source: item.source,
                pdf_link: item.pdf,
                source: item.source,
            });
            let operation_id = Operation.id;
            if (operation_id) {
                //topic
                item["class_topic"].map(async (topicName) => {
                    const [topic, created] = await Topic.findOrCreate({
                        where: { name: topicName },
                        defaults: {
                            id: null,
                            name: topicName,
                        },
                    });
                    let topic_id = topic.id;
                    if (topic_id) {
                        OperationTopic.create({
