                        where: { name: subjectName },
                        defaults: {
                            id: null,
                            name: subjectName,
                        },
                    });
                    let subject_id = subject.id;
                    if (subject_id) {
                        OperationSubject.create({
                            id: null,
                            subject_id,
                            operation_id,
                        });
                    }
                });
            }
            if (operation_id) {
                //grade
                item["class_grade"].map(async (gradeName) => {
                    const [grade, created] = await Grade.findOrCreate({
                        where: { name: gradeName },
                        defaults: {
                            id: null,
                            name: gradeName,
                        },
                    });
                    let grade_id = grade.id;
                    if (grade_id) {
                        OperationGrade.create({
                            id: null,
                            grade_id,
                            operation_id,
