const {
  DataTypes,
  Sequelize,
  Op
} = require("sequelize");
const sequelize = require("../config/database");
const User = require("../models/usedModel");
const Survey = sequelize.define("Survey", {
  id: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
  title: DataTypes.TEXT,
  description: DataTypes.TEXT,
  type: {
    type: DataTypes.ENUM("basic", "in_depth"),
    defaultValue: "basic"
  },
  author_id: {
    type: DataTypes.INTEGER,
    references: {
      model: {
        tableName: "user_info"
      },
      // provide table name
      key: "id"
    },
    allowNull: false,
    onUpdate: "cascade",
    onDelete: "cascade"
  },
  created_at: DataTypes.DATE
}, {
  timestamps: false,
  createdAt: "created_at",
  tableName: "survey",
  modelName: "survey"
});
const SurveyQuestion = sequelize.define("SurveyQuestion", {
  id: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
  survey_id: {
    type: DataTypes.INTEGER,
    references: {
      model: {
        tableName: "survey"
      },
      key: "id"
    },
    allowNull: false,
    onUpdate: "cascade",
    onDelete: "cascade"
  },
  zone: {
    type: DataTypes.ENUM("basic", "edu", "professional"),
    defaultValue: "basic"
  },
  zone_competency: DataTypes.TEXT,
