const { DataTypes, Sequelize, Op } = require("sequelize");
const sequelize = require("../config/database");
const User = require("../models/usedModel");
const Survey = sequelize.define(
    "Survey",
    {
        id: {
            type: DataTypes.INTEGER,
            autoIncrement: true,
            primaryKey: true,
            allowNull: true,
        },
        title: DataTypes.TEXT,
        description: DataTypes.TEXT,
        type: {
            type: DataTypes.ENUM("basic", "in_depth"),
            defaultValue: "basic",
        },
        author_id: {
            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "user_info" }, // provide table name
                key: "id",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
        created_at: DataTypes.DATE,
    },
    {
        timestamps: false,
        createdAt: "created_at",
        tableName: "survey",
        modelName: "survey",
    }
);

const SurveyQuestion = sequelize.define(
    "SurveyQuestion",
    {
        id: {
            type: DataTypes.INTEGER,
            autoIncrement: true,
            primaryKey: true,
            allowNull: true,
        },
        survey_id: {
            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "survey" },
                key: "id",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
        zone: {
            type: DataTypes.ENUM("basic", "edu", "professional"),
            defaultValue: "basic",
        },
        zone_competency: DataTypes.TEXT,
        title: DataTypes.TEXT,
        description: DataTypes.TEXT,
        answers: DataTypes.JSON,
        order: DataTypes.INTEGER,
        author_id: {
            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "user_info" },
                key: "id",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
        created_at: DataTypes.DATE,
    },
    {
        timestamps: false,
        createdAt: "created_at",
        tableName: "survey_question",
        modelName: "surveyQuestion",
    }
);

const SurveyResult = sequelize.define(
    "SurveyResult",
    {
        id: {
            type: DataTypes.INTEGER,
            autoIncrement: true,
            primaryKey: true,
            allowNull: true,
        },
        user_id: {
            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "user_info" },
                key: "id",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
        survey_id: {
            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "survey" },
                key: "id",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
        number_turn: DataTypes.INTEGER,
        answered: DataTypes.JSON,
        result: DataTypes.JSON,
        school_level: DataTypes.TEXT,
        experience: DataTypes.TEXT,
        gender: DataTypes.INTEGER,
        region: DataTypes.TEXT,
        is_last_time: DataTypes.INTEGER,
        aiedap_training: DataTypes.TEXT,
        teacher_career: DataTypes.TEXT,
        teacher_type: DataTypes.TEXT,
        subject: DataTypes.TEXT,
        education_office: DataTypes.TEXT,
        education_office_info: DataTypes.TEXT,
        training_selection: DataTypes.TEXT,
        created_date: DataTypes.DATE,
    },
    {
        timestamps: false,
        createdAt: "created_at",
        tableName: "survey_result",
        modelName: "SurveyResult",
    }
);

SurveyQuestion.belongsTo(Survey, {
    foreignKey: "survey_id",
    as: "survey",
});
SurveyResult.belongsTo(Survey, {
    foreignKey: "survey_id",
    as: "survey",
});
Survey.belongsTo(User, {
    foreignKey: "author_id",
    as: "user",
});
SurveyResult.belongsTo(User, {
    foreignKey: "user_id",
    as: "user",
});

SurveyQuestion.belongsTo(User, {
    foreignKey: "author_id",
    as: "user",
});

module.exports = {
    Survey,
    SurveyQuestion,
    SurveyResult,
    sequelize,
    Sequelize,
    Op,
};

