  difficulty: DataTypes.TEXT,
  objective: DataTypes.TEXT,
  achievement: DataTypes.TEXT,
  source: DataTypes.TEXT,
  pdf_link: DataTypes.TEXT
}, {
  timestamps: false,
  tableName: "class_case",
  modelName: "LessonSample"
});
LessonSample.belongsTo(ClassTopic, {
  foreignKey: "topic_id",
  as: "classTopic"
});
const ModelSearch = sequelize.define("ModelSearch", {
  id: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
  name: DataTypes.TEXT,
  type_id: {
    type: DataTypes.INTEGER,
    references: {
      model: {
        tableName: "class_type"
      },
      key: "id"
    },
    allowNull: false,
    onUpdate: "cascade",
    onDelete: "cascade"
  },
  subject_id: {
    type: DataTypes.INTEGER,
    references: {
      model: {
        tableName: "class_subject"
      },
      key: "id"
    },
    allowNull: false,
    onUpdate: "cascade",
    onDelete: "cascade"
  },
  grade: DataTypes.JSON,
  source: DataTypes.TEXT,
  pdf_link: DataTypes.TEXT,
  description: DataTypes.TEXT,
  apa: DataTypes.TEXT,
  image: DataTypes.TEXT
}, {
  timestamps: false,
  tableName: "model",
  modelName: "ModelSearch"
});
ModelSearch.belongsTo(Type, {
  foreignKey: "type_id",
  as: "type"
});
ModelSearch.belongsTo(Subject, {
  foreignKey: "subject_id",
  as: "subject"
});
//Many-to-Many Association LessonSample, tool

const ClassCaseTool = sequelize.define("ClassCaseTool", {
  id: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true,
    allowNull: true
  },
  case_sno: {
    type: DataTypes.INTEGER,
    references: {
      model: LessonSample,
      key: "sno"
    }
  },
  tool_sno: {
    type: DataTypes.INTEGER,
    references: {
      model: AiedutechTool,
      key: "sno"
    }
  }
}, {
  tableName: "class_case_tool",
  timestamps: false
});
LessonSample.belongsToMany(AiedutechTool, {
  through: ClassCaseTool,
  as: "tools",
  foreignKey: "case_sno",
  otherKey: "tool_sno"
});
AiedutechTool.belongsToMany(LessonSample, {
  through: ClassCaseTool,
