const { DataTypes } = require("sequelize");

const sequelize = require("../config/database");

const { Topic, Subject, Grade, Type } = require("../models/designModel");
const opExp = sequelize.define(
    "opExp",
    {
        sno: {
            type: DataTypes.INTEGER,
            autoIncrement: true,
            primaryKey: true,
            allowNull: true,
        },
        name_kr: DataTypes.TEXT,
        tool_id: DataTypes.INTEGER,
        tech_id: DataTypes.INTEGER,
        core_func: DataTypes.TEXT,
        name_en: DataTypes.TEXT,
        description: DataTypes.TEXT,
        service_link: DataTypes.TEXT,
        case_link: DataTypes.TEXT,
        case_img: DataTypes.TEXT,
    },
    {
        timestamps: false,
        tableName: "op_exp",
        modelName: "opExp",
    }
);

const opExpTechType = sequelize.define(
    "op_exp_tech_type",
    {
        id: {
            type: DataTypes.INTEGER,
            autoIncrement: true,
            primaryKey: true,
            allowNull: true,
        },
        exp_sno: {
            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "op_exp" }, // provide table name
                key: "sno",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
        type_id: {
            type: DataTypes.INTEGER,
            references: {
                model: { tableName: "tool_aiedutech" }, // provide table name
                key: "id",
            },
            allowNull: false,
            onUpdate: "cascade",
            onDelete: "cascade",
        },
    },
    {
        timestamps: false,
        tableName: "op_exp_tech_type",
        modelName: "opExpTechType",
    }
);

const ClassTopic = sequelize.define(
