        case_link: DataTypes.TEXT,
        case_img: DataTypes.TEXT,
    },
    {
        timestamps: false,
        tableName: "aiedutech_tool",
        modelName: "AiedutechTool",
    }
);

const LessonSample = sequelize.define(
    "LessonSample",
    {
        sno: {
            type: DataTypes.INTEGER,
            autoIncrement: true,
            primaryKey: true,
            allowNull: true,
        },
