    otherKey: "tool_sno",
});
AiedutechTool.belongsToMany(LessonSample, {
    through: ClassCaseTool,
    as: "lessons",
    foreignKey: "tool_sno",
    otherKey: "case_sno",
});
//OperationCase table
const OperationCase = sequelize.define(
