const Wishlist = require("../models/wishlistModel");
const {
    opExp,
    AiedutechTool,
    opExpTechType,
    sequelize,
    LessonSample,
    ModelSearch,
    OperationCase,
    OperationTool,
    OperationGrade,
    OperationType,
    OperationSubject,
    OperationTopic,
} = require("../models/searchModel");
const User = require("../models/usedModel");

exports.createOrDeleteWishlist = async (foreign_key_id, user_id, type) => {
    let wishlist = await Wishlist.findOne({
        where: { foreign_key_id, user_id, type },
    });
    if (wishlist == null) {
        return await Wishlist.create({
            id: null,
            user_id,
            foreign_key_id,
            type,
        });
    } else {
        await wishlist.destroy();
        return wishlist;
    }
};
exports.getWishlists = async (paged, limit, user_id) => {
    const offset = (paged - 1) * limit;
    let query = {
        include: [
            {
                model: User,
                as: "user",
            },
        ],
        order: [["created_date", "DESC"]],
    };

    if (paged) {
        query["offset"] = offset;
    }
    if (limit) {
        query["limit"] = limit;
    }
    if (user_id) {
        query["where"] = { user_id };
    }

    const { count, rows } = await Wishlist.findAndCountAll(query);

    //convert pdf link
    const processedData = await Promise.all(
        rows.map(async (item) => {
            // Process tool
            if (item.type == "tool") {
                item.dataValues.info = await AiedutechTool.findOne({
                    where: { sno: item.foreign_key_id },
                    attributes: ["sno", "name_kr", "description", "case_img"],
                });
            }
            // Process lesson
            if (item.type == "lesson") {
                item.dataValues.info = await LessonSample.findOne({
                    where: { sno: item.foreign_key_id },
                    attributes: ["sno", "topic", "objective"],
                });
            }
            // Process operation
            if (item.type == "operation") {
                item.dataValues.info = await OperationCase.findOne({
                    where: { id: item.foreign_key_id },
                    attributes: ["id", "title", "learning_objectives"],
                });
            }
            // Process model
            if (item.type == "model") {
                item.dataValues.info = await ModelSearch.findOne({
                    where: { id: item.foreign_key_id },
                    attributes: ["id", "name", "description", "image"],
                });
            }

            return item;
        })
    );

    const totalPages = Math.ceil(count / limit);

    return {
        items: processedData,
        totalItems: count,
        itemCount: processedData.length,
        itemsPerPage: limit,
        totalPages,
        currentPage: paged,
    };
};
exports.getCommunityWishlists = async (paged, limit) => {
    const offset = (paged - 1) * limit;
    const { count, rows } = await Wishlist.findAndCountAll({
        attributes: [
            "type",
            "foreign_key_id",
            [sequelize.fn("COUNT", sequelize.col("id")), "countWishlists"],
        ],
        group: ["type", "foreign_key_id"],
        order: [[sequelize.fn("MAX", sequelize.col("created_date")), "DESC"]],
        offset: offset,
        limit: limit,
    });

    //convert pdf link
    const processedData = await Promise.all(
        rows.map(async (item) => {
            let info = null;
            switch (item.type) {
                case "tool":
                    info = await AiedutechTool.findOne({
                        where: { sno: item.foreign_key_id },
                        attributes: [
                            "sno",
                            "name_kr",
                            "description",
                            "case_img",
                        ],
                    });
                    break;
                case "lesson":
                    info = await LessonSample.findOne({
                        where: { sno: item.foreign_key_id },
                        attributes: ["sno", "topic", "objective"],
                    });
                    break;
                case "operation":
                    info = await OperationCase.findOne({
                        where: { id: item.foreign_key_id },
                        attributes: ["id", "title", "learning_objectives"],
                    });
                    break;
                case "model":
                    info = await ModelSearch.findOne({
                        where: { id: item.foreign_key_id },
                        attributes: ["id", "name", "description", "image"],
                    });
                    break;
            }
            item.dataValues.info = info;
            return item;
        })
    );

    const totalPages = Math.ceil(count.length / limit);

    return {
        items: processedData,
        totalItems: count.length,
        itemCount: processedData.length,
        itemsPerPage: limit,
        totalPages,
        currentPage: paged,
    };
};

