      if (!hasRequiredRights) {
        throw new ApiError(httpStatus.FORBIDDEN, "Forbidden");
      }
    }
    req.user = userDoc.user;
    next();
  } catch (error) {
    // If there's any error (invalid token, no rights, etc.), pass it to error handler
    next(error);
  }
};
